import React, { useEffect, useState } from 'react';
import axios from 'axios'; // Use axios for more consistent and flexible API requests
import Swal from 'sweetalert2';
import withReactContent from 'sweetalert2-react-content';
import { show_alerta } from '../functions'; // Assuming this function exists and handles alerts

const ShowProducts = () => {
  const url = 'http://localhost:4000/api/usuarios/'; // Ensure correct API URL
  const [usuarios, setUsuarios] = useState([]);

  useEffect(() => {
    getUsuarios();
  }, []);

  const getUsuarios = async () => {
    try {
      const response = await axios.get(url);
      if (response.data && Array.isArray(response.data.body)) {
        setUsuarios(response.data.body);
      } else {
        show_alerta('Error al obtener usuarios', 'error');
      }
    } catch (error) {
      console.error('Error:', error);
      show_alerta('Error al obtener usuarios', 'error');
    }
  };
  

  return (
    <div className='app'>
      <div className='container-fluid'>
        {/* Include other code sections from the original component if needed */}
        <div className='row mt-3'>
          <div className='col-12 col-lg-8 offset-0 offset-lg-12'>
            <div className='table-responsive'>
              <table className='table table-bordered'>
                <thead>
                  <tr>
                    <th>#</th>
                    <th>Nombre</th>
                    <th>Username</th>
                    <th>Email</th>
                    <th>Tel√©fono</th>
                    <th>Sitio Web</th>
                    <th>Acciones</th>
                  </tr>
                </thead>
                <tbody>
                  {usuarios.map((usuario, i) => (
                    <tr key={usuario.id}>
                      <th>{(i + 1)}</th>
                      <th>{usuario.name}</th>
                      <th>{usuario.username}</th>
                      <th>{usuario.email}</th>
                      <th>{usuario.phone}</th>
                      <th>{usuario.website}</th>
                      <th>
                        {/* You can add your button and modal code here if needed */}
                      </th>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ShowProducts;
