import React, { useEffect, useState } from 'react';

const ShowUsers = () => {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    const fetchUsers = async () => {
      const res = await fetch("http://localhost:4000/api/usuarios/");
      const data = await res.json();
      setUsers(data.body);
    };
    fetchUsers();
  }, []);

  return (
    <div className='app'>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Nombre</th>
            <th>Nombre de usuario</th>
            <th>Email</th>
            <th>Tel√©fono</th>
            <th>Sitio web</th>
          </tr>
        </thead>
        <tbody>
          {users.map(user => (
            <tr key={user.id}>
              <td>{user.id}</td>
              <td>{user.name}</td>
              <td>{user.username}</td>
              <td>{user.email}</td>
              <td>{user.phone}</td>
              <td>{user.website}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default ShowUsers;
