import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Swal from 'sweetalert2';
import withReactContent from 'sweetalert2-react-content';
import { show_alerta } from '../functions';

const ShowPorducts = () => {
    const url = 'http://localhost/api-products/';
    const [products, setProducts] = useState([]);
    const [id, setId] = useState('');
    const [name, setName] = useState('');
    const [description, SetDescription] = useState('');
    const [price, setPrice] = useState('');
    const [operation, setOperation] = useState(1);
    const [title, setTitle] = useState('');

    useEffect(() => {
        getProducts();
    }, []);

    const getProducts = async () => {
        const respuesta = await axios.get(url);
        setProducts(respuesta.data);
    }
    const openModal = (op,id, name, description, price) =>{
        setId('');
        setName('');
        SetDescription('');
        setPrice('');
        setOperation(op);
        if(op === 1){
            setTitle('Registrar Producto');
        }
        else if(op === 2){
            setTitle('Editar Producto');
            setId(id);
            setName(name);
            SetDescription(description);
            setPrice(price);
        }
        window.setTimeout(function(){
            document.getElementById('nombre').focus();
        },500);
    }
    const validar = () => {
        var parametros;
        var metodo;
        if(name.trim() === ''){
            show_alerta('Escribe el nombre del producto','warning');
        }
        else if(description.trim() === ''){
            show_alerta('Escribe la descripciÃ³n del producto','warning');
        }
        else if(price.trim() === ''){
            show_alerta('Escribe el precio del producto','warning');
        }
        else{
            if(operation === 1){
                parametros= {name:name.trim(),description: description.trim(),price:price};
                metodo = 'POST';
            }
            if(operation === 1){
                parametros= {name:name.trim(),description: description.trim(),price:price};
                metodo = 'PUT';
            }
        }
    }

    return (
        <div className='app'>
            <div className='container-fluid'>
                <div className='row mt-3'>
                    <div className='col-md-4 offset-4'>
                        <div className='d-grid mx-auto'>
                            <button onClick={()=> openModal(1)} className='btn btn-dark' data-bs-toggle='modal' data-bs-target='#modalProducts'>
                                <i className='fa-solid fa-circle-plus'></i> AÃ±adir
                            </button>
                        </div>
                    </div>
                </div>
                <div className='row mt-3'>
                    <div className='col-12 col-lg-8 offset-0 offset-lg-12'>
                        <div className='table-responsive'>
                            <table className='table table-bordered'>
                                <thead>
                                    <tr><th>#</th><th>PRODUCTOS</th><th>DESCRIPCION</th><th>PRECIO</th><th></th></tr>
                                </thead>
                                <tbody className='table-group-divider'>
                                    {products.map((product, i) => (
                                        <tr key={product.id}>
                                            <th>{(i + 1)}</th>
                                            <th>{product.name}</th>
                                            <th>{product.description}</th>
                                            <th>${new Intl.NumberFormat('es-py').format(product.price)} </th>
                                            <th>
                                                <button onClick={()=> openModal(2,product.id,product.name,product.description,product.price)}
                                                    className='btn btn-warning' data-bs-toggle='modal' data-bs-target='#modalProducts'>
                                                    <i className='fa-solid fa-edit'></i>
                                                </button>
                                                &nbsp;
                                                <button className='btn btn-danger'>
                                                    <i className='fa-solid fa-trash'></i>
                                                </button>
                                            </th>
                                        </tr>
                                    ))}
                                </tbody>
                            </table>
                        </div>
                    </div>
                </div>
            </div>
            <div id='modalProducts' className='modal fade' aria-hidden='true'>
                <div className='modal-dialog'>
                    <div className='modal-content'>
                        <div className='modal-header'>
                            <label className='h5'>{title}</label>
                            <button type='button' className='btn-close' data-bs-dismiss='modal' aria-label='close'></button>
                        </div>
                        <div className='modal-body'>
                            <imput type='hidden' id='id'></imput>
                            <div className='input-group mb-3'>
                                <span className='input-group-text'><i className='fa-solid fa-gift'></i></span>
                                <input type='text' id='nombre' className='form-control' placeholder='Nombre' value={name}
                                    onChange={(e) => setName(e.target.value)}></input>
                            </div>
                            <div className='input-group mb-3'>
                                <span className='input-group-text'><i className='fa-solid fa-comment'></i></span>
                                <input type='text' id='descripcion' className='form-control' placeholder='DescripciÃ³n' value={description}
                                    onChange={(e) => SetDescription(e.target.value)}></input>
                            </div>
                            <div className='input-group mb-3'>
                                <span className='input-group-text'><i className='fa-solid fa-dollar-sign'></i></span>
                                <input type='text' id='precio' className='form-control' placeholder='Precio' value={price}
                                    onChange={(e) => setPrice(e.target.value)}></input>
                            </div>
                            <div className='d-grid col-6 mx-auto'>
                                <button className='btn btn-success'>
                                    <i className='fa-solid fa-save'></i> Guardar
                                </button>
                            </div>
                        </div>
                        <div className='modal-footer'>
                            <button type='button' className='btn btn-secondary' data-bs-dismiss='modal'>Cerrar</button>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default ShowPorducts
