import React, { useEffect, useState } from 'react';
import Swal from 'sweetalert2';
import withReactContent from 'sweetalert2-react-content';
import { show_alerta } from '../functions';

const ShowProducts = () => {
    const [usuarios, setUsuarios] = useState([]);

    useEffect(() => {
        fetch('http://localhost:4000/api/usuarios') // La URL de tu API
            .then(response => {
                if (!response.ok) {
                    throw new Error('Error al obtener los datos');
                }
                return response.json();
            })
            .then(data => {
                setUsuarios(data.body); // Suponiendo que la respuesta tiene una propiedad body que contiene los usuarios
            })
            .catch(error => {
                console.error('Error:', error);
                Swal.fire({
                    icon: 'error',
                    title: 'Error',
                    text: 'Hubo un problema al obtener los datos de los usuarios'
                });
            });
    }, []);

    return (
        <div className='app'>
            <h1>Lista de Usuarios</h1>
            <ul>
                {usuarios.map(usuario => (
                    <li key={usuario.id}>
                        <p>Nombre: {usuario.name}</p>
                        <p>Username: {usuario.username}</p>
                        <p>Email: {usuario.email}</p>
                        <p>Tel√©fono: {usuario.phone}</p>
                        <p>Website: {usuario.website}</p>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default ShowProducts;
